#include <iostream>
constexpr int MAXN = 2e5 + 5;int n, r;char a[MAXN];int leftUnlockDoorIndex = -1, rightUnlockDoorIndex = -1;int main(){freopen("doors.in", "r", stdin);freopen("doors.out", "w", stdout);std::cin >> n >> r;for (auto i = 1; i <= n; i++){std::cin >> a[i];if (leftUnlockDoorIndex == -1 && a[i] == '0'){leftUnlockDoorIndex = i;}}for (auto i = n; i >= 1; i--){if (a[i] == '0'){rightUnlockDoorIndex = i;break;}}auto pos = r;size_t count = 0;bool flag = true;bool walkedFlag = false;bool isFirstRun = true;bool backFlag = false;while (true){if (backFlag == true && pos == r){break;}if (leftUnlockDoorIndex == pos){walkedFlag = !walkedFlag;flag = !flag;a[pos] = '1';count++;}else if (rightUnlockDoorIndex == pos){walkedFlag = !walkedFlag;flag = !flag;a[pos] = '1';count++;backFlag = true;}else{if (walkedFlag){if (a[pos] == '0'){a[pos] = '1';count++;}}else{if (a[pos] == '1'){a[pos] = '0';count++;}}}if (pos == r && !isFirstRun){walkedFlag = !walkedFlag;}isFirstRun = false;if (flag){pos--;}else{pos++;}}std::cout << count << std::endl;return 0;}